<?php/** * Template Name: Contact Page */get_header(); ?>	<?php while ( have_posts() ) : the_post(); ?>			<div class="container">			<div class="row-fluid">				<?php if ( function_exists( 'dewitech_breadcrumbs' ) ) dewitech_breadcrumbs(); ?>			</div><!--/.row -->		</div><!--/.container -->   	<article class="container">        <div class="row content">			<div class="col-md-8">				<header class="page-title">					<h2><?php the_title();?></h2>				</header>				<?php the_content();?>				<?php endwhile; // end of the loop. ?>				<div id="map"></div><!-- /#map -->				<div class="row">						<div id="contact-info" class="col-md-6">							<h4>Contact Info</h4>							<p><strong>Address:</strong>								<?php echo get_option('dt_mapaddr'); ?>, <?php echo get_option('dt_mapprov'); ?>, 								<?php echo get_option('dt_mapcountry'); ?>.							</p>							<p><strong>Phone:</strong> <?php echo get_option('dt_footphone'); ?></p>							<p>								<strong>Email:</strong> <a href="mailto:<?php echo get_option('dt_footmail'); ?>"><?php echo get_option('dt_footmail'); ?></a><br/>								<strong>Website:</strong> <a href="<?php echo home_url(); ?>"><?php echo home_url(); ?></a>							</p>						</div>						<div id="contact-form" class="col-md-6">						<h4>Contact Form</h4>				<form id="form" class="contact" action="contact.php" method="post" name="contactform">					<fieldset>						<input class="col-md-12" id="name" type="text" onBlur="if(this.value==''){this.value = 'Your Name*';}" value="Your Name*" onFocus="if(this.value == 'Your Name*') { this.value = ''; }" name="name">						<input class="col-md-12" id="email" type="text" onBlur="if(this.value==''){this.value = 'Email*';}" value="Email*" onFocus="if(this.value == 'Email*') { this.value = ''; }" name="email">						<textarea class="field col-md-12" id="msg" onBlur="if(this.value==''){this.value = 'Message*';}" rows="8" onFocus="if(this.value == 'Message*') { this.value = ''; }" name="msg">Message*</textarea>						<input type="hidden" value="true" name="browser_check">						<button class="btn btn-green col-md-12" id="submit" type="submit" name="submit">Submit a message</button>					</fieldset>				</form>	<!--form.#contact_form-->						</div>				</div>			</div>			<div class="col-md-4">				<?php get_sidebar();?>			</div>		</div>	</article><?php get_footer(); ?>